// CartPage.jsx
import React from 'react';
import { Trash2 } from 'lucide-react';
import { Link } from 'react-router-dom';

const CartPage = ({ cart, removeFromCart }) => {
  const totalAmount = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="container mx-auto px-4 py-8 min-h-screen bg-gray-100">
      <h2 className="text-2xl font-bold mb-6">Your Cart</h2>

      {cart.length === 0 ? (
        <div className="bg-white p-6 rounded shadow text-center">
          <p className="text-gray-600 text-lg">ðŸ›’ Your cart is empty.</p>
          <Link
            to="/"
            className="mt-4 inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Continue Shopping
          </Link>
        </div>
      ) : (
        <div className="space-y-4">
          {cart.map((item) => (
            <div
              key={item.id}
              className="bg-white p-4 rounded-lg shadow flex items-center justify-between"
            >
              <div className="flex items-center space-x-4">
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-20 h-20 object-cover rounded"
                />
                <div>
                  <h3 className="font-semibold text-lg">{item.name}</h3>
                  <p className="text-gray-700">
                    â‚¹{item.price.toLocaleString()} x {item.quantity}
                  </p>
                  <p className="text-sm text-gray-500">
                    Total: â‚¹{(item.price * item.quantity).toLocaleString()}
                  </p>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="text-red-600 hover:text-red-800"
              >
                <Trash2 />
              </button>
            </div>
          ))}

          <div className="text-right font-bold text-xl text-gray-800">
            Grand Total: â‚¹{totalAmount.toLocaleString()}
          </div>

          <div className="text-right mt-4">
            <button className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700">
              Proceed to Checkout
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
