import React from 'react';

const Cart = ({ cart }) => {
  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Your Cart</h2>
      {cart.length === 0 ? (
        <p className="text-gray-600">Your cart is empty.</p>
      ) : (
        <div className="space-y-6">
          {cart.map(item => (
            <div key={item.id} className="bg-white p-4 rounded shadow flex items-center justify-between">
              <div>
                <h3 className="font-semibold">{item.name}</h3>
                <p>₹{item.price.toLocaleString()} × {item.quantity}</p>
              </div>
              <div className="font-bold text-gray-800">₹{(item.price * item.quantity).toLocaleString()}</div>
            </div>
          ))}
          <div className="text-right font-semibold text-xl text-gray-800">
            Total: ₹{total.toLocaleString()}
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
