import React, { useState, useEffect } from 'react';

const bannerSlides = [
  {
    id: 1,
    image: 'https://via.placeholder.com/1200x400/007bff/ffffff?text=Summer+Sale',
    title: 'Summer Sale is On!',
    subtitle: 'Get up to 50% off on top brands',
  },
  {
    id: 2,
    image: 'https://via.placeholder.com/1200x400/28a745/ffffff?text=New+Arrivals',
    title: 'New Arrivals',
    subtitle: 'Check out the latest products in store',
  },
  {
    id: 3,
    image: 'https://via.placeholder.com/1200x400/ffc107/000000?text=Best+Deals',
    title: 'Best Deals of the Season',
    subtitle: 'Limited time offer. Hurry up!',
  },
];

const Banner = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentSlide((prev) => (prev + 1) % bannerSlides.length);
    }, 5000);
    return () => clearTimeout(timer);
  }, [currentSlide]);

  return (
    <div className="relative h-64 md:h-80 overflow-hidden">
      {bannerSlides.map((slide, index) => (
        <div
          key={slide.id}
          className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
            index === currentSlide ? 'opacity-100' : 'opacity-0'
          }`}
        >
          <img
            src={slide.image}
            alt={slide.title}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-black bg-opacity-40 flex items-center justify-center">
            <div className="text-center text-white px-4">
              <h2 className="text-2xl md:text-4xl font-bold mb-2">{slide.title}</h2>
              <p className="text-lg md:text-xl">{slide.subtitle}</p>
            </div>
          </div>
        </div>
      ))}

      {/* Dots */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {bannerSlides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentSlide(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              index === currentSlide ? 'bg-white' : 'bg-white bg-opacity-50'
            }`}
          />
        ))}
      </div>
    </div>
  );
};

export default Banner;
