import React, { useState, createContext, useContext } from 'react';
import { Routes, Route, useNavigate, Link } from 'react-router-dom';
import { ShoppingCart, X, Menu } from 'lucide-react';

// --- Cart Context ---
const CartContext = createContext();
const useCart = () => useContext(CartContext);

// --- Sample Products ---
const products = [
  {
    id: 1,
    name: "iPhone 14 Pro",
    price: 79999,
    image: "https://images.unsplash.com/photo-1592750475338-74b7b21085ab?w=300&h=300&fit=crop",
  },
  {
    id: 2,
    name: "MacBook Air M2",
    price: 119999,
    image: "https://images.unsplash.com/photo-1541807084-5c52b6b3adef?w=300&h=300&fit=crop",
  },
];

// --- App Component ---
const App = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart((prev) => {
      const exists = prev.find((item) => item.id === product.id);
      if (exists) {
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart }}>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/cart" element={<CartPage />} />
      </Routes>
    </CartContext.Provider>
  );
};

// --- Header ---
const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const { cart } = useCart();

  return (
    <header className="bg-blue-600 text-white sticky top-0 z-50 shadow-lg p-4">
      <div className="flex justify-between items-center">
        <div className="flex items-center space-x-3">
          <button className="md:hidden" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            {isMenuOpen ? <X /> : <Menu />}
          </button>
          <h1 className="text-xl font-bold"><Link to="/">Flipkart</Link></h1>
        </div>

        <div className="flex items-center space-x-4">
          <button onClick={() => navigate('/cart')} className="relative">
            <ShoppingCart />
            {cart.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">
                {cart.reduce((sum, item) => sum + item.quantity, 0)}
              </span>
            )}
          </button>
        </div>
      </div>
    </header>
  );
};

// --- Home Page ---
const HomePage = () => {
  const { addToCart } = useCart();

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <main className="p-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <div key={product.id} className="bg-white p-4 rounded shadow">
            <img src={product.image} alt={product.name} className="w-full h-40 object-cover rounded" />
            <h3 className="mt-2 text-lg font-semibold">{product.name}</h3>
            <p className="text-gray-700 font-bold mb-2">₹{product.price.toLocaleString()}</p>
            <button
              onClick={() => addToCart(product)}
              className="bg-orange-500 text-white px-4 py-2 rounded hover:bg-orange-600 w-full"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </main>
    </div>
  );
};

// --- Cart Page ---
const CartPage = () => {
  const { cart, removeFromCart } = useCart();
  const navigate = useNavigate();
  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
        {cart.length === 0 ? (
          <p className="text-gray-600">Your cart is empty.</p>
        ) : (
          <div className="space-y-4">
            {cart.map((item) => (
              <div key={item.id} className="bg-white p-4 rounded shadow flex justify-between items-center">
                <div>
                  <h3 className="font-semibold">{item.name}</h3>
                  <p>Price: ₹{item.price.toLocaleString()}</p>
                  <p>Quantity: {item.quantity}</p>
                </div>
                <img src={item.image} alt={item.name} className="w-20 h-20 object-cover rounded" />
                <button
                  onClick={() => removeFromCart(item.id)}
                  className="text-red-600 hover:underline"
                >
                  Remove
                </button>
              </div>
            ))}
            <div className="text-right font-bold text-lg mt-4">
              Total: ₹{total.toLocaleString()}
            </div>
            <button
              onClick={() => navigate('/')}
              className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Continue Shopping
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
