import React, { useState } from 'react';
import { Heart, Star, Truck, Shield, RotateCcw } from 'lucide-react';


const Header = () => (
  <header className="bg-blue-600 text-white sticky top-0 z-50 shadow-lg">
    <div className="container mx-auto px-4">
      <div className="flex items-center justify-between h-16">
        <h1 className="text-xl font-bold">Flipkart Clone</h1>
      </div>
    </div>
  </header>
);

 
 

const App = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart((prev) => {
      const exists = prev.find(item => item.id === product.id);
      if (exists) {
        return prev.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const products = [
    {
      id: 1,
      name: 'Smartphone XYZ',
      image: 'https://via.placeholder.com/300',
      price: 14999,
      originalPrice: 17999,
      rating: 4.3,
      reviews: 1200,
      discount: 17,
      inStock: true
    },
    {
      id: 2,
      name: 'Wireless Headphones',
      image: 'https://via.placeholder.com/300',
      price: 2499,
      originalPrice: 2999,
      rating: 4.5,
      reviews: 450,
      discount: 15,
      inStock: true
    },
    {
      id: 3,
      name: 'Laptop ABC',
      image: 'https://via.placeholder.com/300',
      price: 59999,
      originalPrice: 65999,
      rating: 4.1,
      reviews: 800,
      discount: 9,
      inStock: false
    }
  ];

  return (
    <div className="bg-gray-100 min-h-screen">
      <Header />
      <Features />
      <section className="py-8">
        <div className="container mx-auto px-4">
          <h2 className="text-2xl font-semibold mb-6 text-gray-800">Featured Products</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {products.map(product => (
              <ProductCard key={product.id} product={product} addToCart={addToCart} />
            ))}
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default App;
