import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { Heart, Star, Truck, Shield, RotateCcw, ShoppingCart } from 'lucide-react';

import Header from './Header';
import Features from './Features';
import Footer from './Footer';
import ProductCard from './ProductCard';
import Cart from './Cart';

const App = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart((prev) => {
      const exists = prev.find(item => item.id === product.id);
      if (exists) {
        return prev.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const products = [
    {
      id: 1,
      name: 'Smartphone XYZ',
      image: 'https://via.placeholder.com/300',
      price: 14999,
      originalPrice: 17999,
      rating: 4.3,
      reviews: 1200,
      discount: 17,
      inStock: true
    },
    {
      id: 2,
      name: 'Wireless Headphones',
      image: 'https://via.placeholder.com/300',
      price: 2499,
      originalPrice: 2999,
      rating: 4.5,
      reviews: 450,
      discount: 15,
      inStock: true
    },
    {
      id: 3,
      name: 'Laptop ABC',
      image: 'https://via.placeholder.com/300',
      price: 59999,
      originalPrice: 65999,
      rating: 4.1,
      reviews: 800,
      discount: 9,
      inStock: false
    }
  ];

  return (
    <Router>
      <div className="bg-gray-100 min-h-screen">
        {/* Simple Header with Cart Link */}
        <header className="bg-blue-600 text-white sticky top-0 z-50 shadow-lg">
          <div className="container mx-auto px-4 flex justify-between items-center h-16">
            <Link to="/" className="text-xl font-bold">Flipkart Clone</Link>
            <Link to="/cart" className="relative">
              <ShoppingCart />
              {cart.length > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                  {cart.length}
                </span>
              )}
            </Link>
          </div>
        </header>

        <Routes>
          <Route path="/" element={
            <>
              <Features />
              <section className="py-8">
                <div className="container mx-auto px-4">
                  <h2 className="text-2xl font-semibold mb-6 text-gray-800">Featured Products</h2>
                  <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                    {products.map(product => (
                      <ProductCard key={product.id} product={product} addToCart={addToCart} />
                    ))}
                  </div>
                </div>
              </section>
            </>
          } />
          <Route path="/cart" element={<Cart cart={cart} />} />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
};

export default App;
