import React, { useState, useEffect } from 'react';
import { Search, ShoppingCart, User, Heart, Star, Menu, X, Filter, ChevronDown, Truck, Shield, RotateCcw } from 'lucide-react';

const FlipkartEcommerce = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [priceRange, setPriceRange] = useState('all');
  const [sortBy, setSortBy] = useState('popularity');

  // Sample product data
  const products = [
    {
      id: 1,
      name: "iPhone 14 Pro",
      price: 79999,
      originalPrice: 89999,
      image: "https://images.unsplash.com/photo-1592750475338-74b7b21085ab?w=300&h=300&fit=crop",
      rating: 4.5,
      reviews: 2341,
      category: "smartphones",
      discount: 11,
      inStock: true
    },
    {
      id: 2,
      name: "Samsung Galaxy S23",
      price: 74999,
      originalPrice: 84999,
      image: "https://images.unsplash.com/photo-1610792516307-ea270f416afc?w=300&h=300&fit=crop",
      rating: 4.3,
      reviews: 1876,
      category: "smartphones",
      discount: 12,
      inStock: true
    },
    {
      id: 3,
      name: "MacBook Air M2",
      price: 119999,
      originalPrice: 134999,
      image: "https://images.unsplash.com/photo-1541807084-5c52b6b3adef?w=300&h=300&fit=crop",
      rating: 4.7,
      reviews: 987,
      category: "laptops",
      discount: 11,
      inStock: true
    },
    {
      id: 4,
      name: "Sony WH-1000XM4",
      price: 24999,
      originalPrice: 29999,
      image: "https://images.unsplash.com/photo-1583394838336-acd977736f90?w=300&h=300&fit=crop",
      rating: 4.6,
      reviews: 3421,
      category: "headphones",
      discount: 17,
      inStock: true
    },
    {
      id: 5,
      name: "Dell XPS 13",
      price: 89999,
      originalPrice: 99999,
      image: "https://images.unsplash.com/photo-1496181133206-80ce9b88a853?w=300&h=300&fit=crop",
      rating: 4.4,
      reviews: 654,
      category: "laptops",
      discount: 10,
      inStock: false
    },
    {
      id: 6,
      name: "AirPods Pro",
      price: 19999,
      originalPrice: 24999,
      image: "https://images.unsplash.com/photo-1572569511254-d8f925fe2cbb?w=300&h=300&fit=crop",
      rating: 4.5,
      reviews: 2876,
      category: "headphones",
      discount: 20,
      inStock: true
    }
  ];

  const categories = [
    { id: 'all', name: 'All Categories' },
    { id: 'smartphones', name: 'Smartphones' },
    { id: 'laptops', name: 'Laptops' },
    { id: 'headphones', name: 'Headphones' }
  ];

  const bannerSlides = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1607082348824-0a96f2a4b9da?w=800&h=400&fit=crop",
      title: "Big Billion Days Sale",
      subtitle: "Up to 80% off on Electronics"
    },
    {
      id: 2,
      image: "https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=800&h=400&fit=crop",
      title: "Mobile Bonanza",
      subtitle: "Latest smartphones at best prices"
    },
    {
      id: 3,
      image: "https://images.unsplash.com/photo-1527864550417-7fd91fc51a46?w=800&h=400&fit=crop",
      title: "Laptop Festival",
      subtitle: "Premium laptops with great deals"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % bannerSlides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  const addToCart = (product) => {
    setCart(prev => {
      const existingItem = prev.find(item => item.id === product.id);
      if (existingItem) {
        return prev.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart(prev => prev.filter(item => item.id !== productId));
  };

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesPrice = priceRange === 'all' || 
      (priceRange === 'under25k' && product.price < 25000) ||
      (priceRange === '25k-50k' && product.price >= 25000 && product.price < 50000) ||
      (priceRange === '50k-100k' && product.price >= 50000 && product.price < 100000) ||
      (priceRange === 'above100k' && product.price >= 100000);
    
    return matchesCategory && matchesSearch && matchesPrice;
  });

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case 'price-low':
        return a.price - b.price;
      case 'price-high':
        return b.price - a.price;
      case 'rating':
        return b.rating - a.rating;
      default:
        return 0;
    }
  });


  

  
  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <MobileMenu />
      <Banner />
      
      <main className="container mx-auto px-4 py-8">
        <Filters />
        
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {sortedProducts.map(product => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
        
        {sortedProducts.length === 0 && (
          <div className="text-center py-12">
            <h3 className="text-lg font-semibold text-gray-600 mb-2">No products found</h3>
            <p className="text-gray-500">Try adjusting your filters or search terms</p>
          </div>
        )}
      </main>
      
      <Features />
      <Footer />
    </div>
  );
};

export default FlipkartEcommerce;
